                                                                                    heldOrders: AppOrder[];
                                                                                      setHeldOrders: React.Dispatch<React.SetStateAction<AppOrder[]>>;
                                                                                      
                                                                                        customers: Customer[];
                                                                                          updateCustomer: (customerId: string, updates: Partial<Customer>) => void;
                                                                                          
